sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;
}

sp_widget.script:
data.typeAheadSearch = $sp.getWidget('coretech-typeahead-search', options.typeahead_search);
(function() {
	var topic = $sp.getParameter('topic');
	data.topicName = '';

	if(topic == 'Topic-AccountPass'){
		data.topicName = 'Accounts & Passwords';
	} else if(topic == 'Topic-Applications'){
		data.topicName = 'Applications';
	} else if(topic == 'Topic-EmailCalendar'){
		data.topicName = 'Email & Calendar';
	} else if(topic == 'Topic-DeskLapPer'){
		data.topicName = 'Desktops, Laptops & Peripherals';
	} else if(topic == 'Topic-NetworkPhone'){
		data.topicName = 'Network, WiFi & Phones';
	} else if(topic == 'Topic-Printing'){
		data.topicName = 'Printing';
	} else if(topic == 'Topic-Security'){
		data.topicName = 'Security';
	}


})();
