sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	var what_needed = g_form.getValue('u_what_needed');

	// clear all the choice list values from what needed field.

	g_form.clearOptions('u_what_needed');

	//If the value of request type field is  hr, add two hr choices and one other choice list 

	if(newValue == 'hr'){
		g_form.clearOptions('u_what_needed');
		g_form.addOption('u_what_needed','hr1','Human Resources 1');
		g_form.addOption('u_what_needed','hr2','Human Resources 2');
		g_form.addOption('u_what_needed','other','Other');

	}
	//If the value of request type field is  facilities, add two facilities choices and one other choice list 
	if(newValue == 'facilities'){

		g_form.addOption('u_what_needed','facilities1','Facilities 1');
		g_form.addOption('u_what_needed','facilities2','Facilities 2');
		g_form.addOption('u_what_needed','other','Other');

	}
	//If the value of request type field is  legal, add two legal choices and one other choice list 
	if(newValue == 'legal'){

		g_form.addOption('u_what_needed','legal1','Legal 1');
		g_form.addOption('u_what_needed','legal2','Legal 2');
		g_form.addOption('u_what_needed','other','Other');

	}

	// If a form is Loading and its not a new record, set value to the value of u_what_needed to the previous value which we get before.

	if(isLoading || !g_form.isNewRecord()){
		g_form.setValue('u_what_needed',what_needed);
	}


}
